static void set_key(char* key, int index)
{
	strcpy(key_buffer[index], key);
}

static char *get_key(int index)
{
	return key_buffer[index];
}

static int salt_hash(void *salt)
{
	return ((int*)salt)[0] & (SALT_HASH_SIZE - 1);
}


